import { useState } from 'react';
import './App.css';
import Bill from './components/Bill';
import {SelectTip} from './components/SelectTip';
import NumberOfPeople from './components/NumberOfPeople';
import TipAmount from './components/TipAmount';

function App() {
    const [percentAtual, setPercentAtual] = useState(0)
    const [inputBill, setInputBill] = useState(0)

    return (
        <section id="calculator">
            <div>
                <Bill setInputBill={setInputBill}/>
                <SelectTip setPercentAtual={setPercentAtual} />
                <NumberOfPeople />
            </div>
            <div>
                <TipAmount setInputBill={setInputBill} setPercentAtual={setPercentAtual}/>
                <div>Total</div>
                <button>Reset</button>
            </div>
        </section>
    );
}

export default App;
