const SelectTip = () => {
    let percent = 0

    const ChangePercent = (evt) => {
        percent = evt.target.value
    };

    return (
        <div>
            <small>Select Tip %</small>
            <button onClick={ChangePercent} value="0.05">5%</button>
            <button onClick={ChangePercent} value="0.1">10%</button>
            <button onClick={ChangePercent} value="0.15">15%</button>
            <button onClick={ChangePercent} value="0.25">25%</button>
            <button onClick={ChangePercent} value="0.5">50%</button>
            <input type="text" placeholder="Custom" />
        </div>
    );
}

export default SelectTip
